import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import io from "socket.io-client";
import "./ShoppingList.css";

const API_URL = "http://localhost:5001"; // Базовый URL сервера
const socket = io(API_URL, { autoConnect: false }); // Инициализация Socket.IO

const ShoppingList = () => {
  const { listId } = useParams(); // Получаем listId из URL
  const navigate = useNavigate();
  const [items, setItems] = useState([]); // Список элементов
  const [newItem, setNewItem] = useState(""); // Новый элемент (ввод)
  const [members, setMembers] = useState([]); // Участники списка
  const [owner, setOwner] = useState(""); // ID владельца
  const [ownerEmail, setOwnerEmail] = useState(""); // Email владельца
  const [listName, setListName] = useState(""); // Название списка
  const [currentUserId, setCurrentUserId] = useState(""); // ID текущего пользователя
  const [notification, setNotification] = useState(""); // Уведомления об ошибках/успехе

  // Получение заголовков авторизации
  const getAuthHeaders = () => ({
    headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
  });

  // Загрузка данных списка
  const fetchList = async () => {
    try {
      console.log("Fetching list:", listId);
      const res = await axios.get(`${API_URL}/lists/${listId}`, getAuthHeaders());
      console.log("List data:", res.data);
      setItems(res.data.items || []);
      setMembers(res.data.members || []);
      setOwner(res.data.owner);
      setOwnerEmail(res.data.ownerEmail);
      setListName(res.data.name);
      setCurrentUserId(res.data.currentUserId);
    } catch (err) {
      console.error("Error fetching list:", err.response?.data || err);
      const errorMessage =
        err.response?.status === 403
          ? "Вы не участник этого списка"
          : err.response?.data?.error || "Ошибка при загрузке списка";
      setNotification(errorMessage);
      setTimeout(() => setNotification(""), 3000);
      if (err.response?.status === 403 || err.response?.status === 404) {
        navigate("/dashboard");
      }
    }
  };

  // Подключение к Socket.IO при монтировании компонента
  useEffect(() => {
    console.log("Socket connecting for list:", listId, "user:", localStorage.getItem("userId"));
    socket.connect();
    socket.emit("joinList", { listId, userId: localStorage.getItem("userId") });

    // Обработка события listUpdated
    socket.on("listUpdated", (data) => {
      console.log("Received listUpdated:", data);
      setItems(data.items || []);
      setMembers(data.members || []);
      setListName(data.name || listName);
    });

    // Загрузка списка при монтировании
    fetchList();

    // Очистка при размонтировании
    return () => {
      console.log("Cleaning up socket listeners for list:", listId);
      socket.off("listUpdated");
      socket.disconnect();
    };
  }, [listId]);

  // Добавление нового элемента
  const addItem = async () => {
    if (!newItem) return;
    try {
      console.log("Adding item:", newItem, "to list:", listId);
      const res = await axios.post(
        `${API_URL}/lists/items/${listId}`,
        { name: newItem },
        getAuthHeaders()
      );
      console.log("Added items:", res.data.items);
      setItems(res.data.items);
      socket.emit("updateList", { listId, items: res.data.items });
      setNewItem("");
    } catch (err) {
      console.error("Error adding item:", err.response?.data || err);
      const errorMessage =
        err.response?.status === 404
          ? "Список не найден"
          : err.response?.data?.error || "Ошибка при добавлении товара";
      setNotification(errorMessage);
      setTimeout(() => setNotification(""), 3000);
    }
  };

  // Обновление статуса элемента (чекбокс)
  const toggleItemCompletion = async (itemId, completed) => {
    try {
      console.log("Toggling item:", itemId, "to completed:", completed);
      const res = await axios.put(
        `${API_URL}/lists/items/${itemId}/complete`,
        { completed },
        getAuthHeaders()
      );
      console.log("Updated items:", res.data.items);
      setItems(res.data.items);
      socket.emit("updateList", { listId, items: res.data.items });
    } catch (err) {
      console.error("Error toggling item completion:", err.response?.data || err);
      const errorMessage =
        err.response?.status === 404
          ? "Элемент или список не найден"
          : err.response?.data?.error || "Ошибка при обновлении статуса товара";
      setNotification(errorMessage);
      setTimeout(() => setNotification(""), 3000);
    }
  };

  // Удаление отмеченных элементов
  const deleteCompletedItems = async () => {
    try {
      console.log("Deleting completed items from list:", listId);
      const res = await axios.delete(
        `${API_URL}/lists/${listId}/items/completed`,
        { headers: getAuthHeaders().headers }
      );
      console.log("Updated items after deletion:", res.data.items);
      setItems(res.data.items);
      socket.emit("updateList", { listId, items: res.data.items });
    } catch (err) {
      console.error("Error deleting completed items:", err.response?.data || err);
      const errorMessage =
        err.response?.status === 404
          ? "Список не найден"
          : err.response?.data?.error || "Ошибка при удалении отмеченных товаров";
      setNotification(errorMessage);
      setTimeout(() => setNotification(""), 3000);
    }
  };

  // Вступление в список
  const joinList = async () => {
    try {
      console.log("Joining list:", listId);
      const res = await axios.post(
        `${API_URL}/lists/join/${listId}`,
        {},
        getAuthHeaders()
      );
      console.log("Join list response:", res.data);
      setNotification(res.data.message);
      setTimeout(() => setNotification(""), 3000);
      // Загружаем данные списка после вступления
      await fetchList();
      // Подключаемся к комнате Socket.IO
      socket.emit("joinList", { listId, userId: localStorage.getItem("userId") });
    } catch (err) {
      console.error("Error joining list:", err.response?.data || err);
      const errorMessage =
        err.response?.status === 404
          ? "Список не найден"
          : err.response?.data?.error || "Ошибка при вступлении в список";
      setNotification(errorMessage);
      setTimeout(() => setNotification(""), 3000);
    }
  };

  // Выход из списка
  const leaveList = async () => {
    try {
      console.log("Leaving list:", listId);
      const res = await axios.post(
        `${API_URL}/lists/${listId}/leave`,
        {},
        getAuthHeaders()
      );
      console.log("Leave list response:", res.data);
      setNotification(res.data.message);
      setTimeout(() => setNotification(""), 3000);
      navigate("/dashboard");
    } catch (err) {
      console.error("Error leaving list:", err.response?.data || err);
      const errorMessage =
        err.response?.data?.error || "Ошибка при выходе из списка";
      setNotification(errorMessage);
      setTimeout(() => setNotification(""), 3000);
    }
  };

  // Удаление участника (для владельца)
  const removeMember = async (userId) => {
    try {
      console.log("Removing member:", userId, "from list:", listId);
      const res = await axios.delete(
        `${API_URL}/lists/${listId}/members/${userId}`,
        getAuthHeaders()
      );
      console.log("Remove member response:", res.data);
      setNotification(res.data.message);
      setTimeout(() => setNotification(""), 3000);
      // Обновляем список участников
      setMembers(members.filter((m) => m.userId !== userId));
      socket.emit("updateList", { listId, members: members.filter((m) => m.userId !== userId) });
    } catch (err) {
      console.error("Error removing member:", err.response?.data || err);
      const errorMessage =
        err.response?.data?.error || "Ошибка при удалении участника";
      setNotification(errorMessage);
      setTimeout(() => setNotification(""), 3000);
    }
  };

  // Обновление названия списка (для владельца)
  const updateListName = async () => {
    try {
      console.log("Updating list name to:", listName, "for list:", listId);
      const res = await axios.put(
        `${API_URL}/lists/${listId}`,
        { name: listName },
        getAuthHeaders()
      );
      console.log("Update list name response:", res.data);
      setNotification(res.data.message);
      setTimeout(() => setNotification(""), 3000);
    } catch (err) {
      console.error("Error updating list name:", err.response?.data || err);
      const errorMessage =
        err.response?.data?.error || "Ошибка при обновлении названия списка";
      setNotification(errorMessage);
      setTimeout(() => setNotification(""), 3000);
    }
  };

  return (
    <div className="container">
      <div className="list-header">
        <button className="button-secondary" onClick={() => navigate("/dashboard")}>
          Назад
        </button>
        <button className="button-danger" onClick={leaveList}>
          Выйти
        </button>
      </div>
      {notification && <div className="notification">{notification}</div>}
      <h1>
        <input
          type="text"
          value={listName}
          onChange={(e) => setListName(e.target.value)}
          onBlur={updateListName}
          disabled={ownerId !== currentUserId}
          className="list-title-input"
        />
      </h1>
      <p>Владелец: {ownerEmail}</p>
      {ownerId === currentUserId && (
        <button className="button-danger" onClick={deleteList}>
          Удалить список
        </button>
      )}
      <div className="list-section">
        <h2>Товары</h2>
        <input
          type="text"
          value={newItem}
          onChange={(e) => setNewItem(e.target.value)}
          placeholder="Добавить товар"
        />
        <button className="button-primary" onClick={addItem}>
          Добавить
        </button>
        <button className="button-danger" onClick={deleteCompletedItems}>
          Удалить отмеченные
        </button>
        <ul className="item-list">
          {items.map((item) => (
            <li key={item._id} className={item.completed ? "completed" : ""}>
              <input
                type="checkbox"
                checked={item.completed}
                onChange={(e) => toggleItemCompletion(item._id, e.target.checked)}
              />
              {item.name}
            </li>
          ))}
        </ul>
      </div>
      <div className="list-section">
        <h2>Участники</h2>
        <ul className="member-list">
          {members.map((member) => (
            <li key={member.userId}>
              {member.email}
              {ownerId === currentUserId && member.userId !== ownerId && (
                <button
                  className="button-danger"
                  onClick={() => removeMember(member.userId)}
                >
                  Удалить
                </button>
              )}
            </li>
          ))}
        </ul>
        <button className="button-primary" onClick={copyShareLink}>
          Поделиться
        </button>
      </div>
    </div>
  );
}

export default ShoppingList;
