import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import io from "socket.io-client";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5001";
const socket = io(API_URL, { autoConnect: false });

function ShoppingList() {
  const { listId } = useParams();
  const navigate = useNavigate();
  const [listName, setListName] = useState("Новый список");
  const [items, setItems] = useState([]);
  const [members, setMembers] = useState([]);
  const [ownerId, setOwnerId] = useState("");
  const [ownerEmail, setOwnerEmail] = useState("");
  const [currentUserId, setCurrentUserId] = useState("");
  const [newItem, setNewItem] = useState("");
  const [notification, setNotification] = useState("");

  const getAuthHeaders = () => ({
    headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
  });

  useEffect(() => {
    const userId = localStorage.getItem("userId");
    if (!userId) {
      console.error("No userId in localStorage");
      navigate("/login");
      return;
    }

    socket.connect();
    console.log("Socket connecting for list:", listId, "user:", userId);
    socket.emit("joinList", listId);
    socket.emit("joinUser", userId);

    // Загрузка списка
    axios
      .get(`${API_URL}/lists/${listId}`, getAuthHeaders())
      .then((res) => {
        console.log("List data:", res.data);
        setItems(res.data.items || []);
        setMembers(res.data.members.sort((a, b) => a.joinedAt - b.joinedAt));
        setOwnerId(res.data.owner);
        setOwnerEmail(res.data.ownerEmail || "Неизвестно");
        setListName(res.data.name);
        setCurrentUserId(res.data.currentUserId);
      })
      .catch((err) => {
        console.error("Error fetching list:", err.response?.data || err);
        if (err.response?.status === 404) {
          setNotification("Список не найден или вы были удалены");
          setTimeout(() => navigate("/dashboard"), 3000);
        } else {
          navigate("/dashboard");
        }
      });

    // Обновление списка
    socket.on("listUpdated", ({ items, members, name }) => {
      console.log("Received listUpdated:", { items, members, name });
      setItems(items || []);
      setMembers(members ? members.sort((a, b) => a.joinedAt - b.joinedAt) : []);
      if (name) setListName(name);
    });

    // Уведомление об удалении
    socket.on("kickedFromList", ({ listId: kickedListId }) => {
      console.log("Received kickedFromList:", kickedListId, "Current listId:", listId);
      if (kickedListId === listId) {
        console.log("Setting notification: Владелец списка удалил вас");
        setNotification("Владелец списка удалил вас");
        setTimeout(() => {
          console.log("Navigating to /dashboard");
          navigate("/dashboard");
        }, 3000);
      }
    });

    return () => {
      console.log("Cleaning up socket listeners for list:", listId);
      socket.off("listUpdated");
      socket.off("kickedFromList");
      socket.disconnect();
    };
  }, [listId, navigate]);

  const addItem = async () => {
    if (!newItem) return;
    try {
      const res = await axios.post(
        `${API_URL}/items/${listId}`,
        { name: newItem },
        getAuthHeaders()
      );
      setItems(res.data.items);
      socket.emit("updateList", { listId, items: res.data.items });
      setNewItem("");
    } catch (err) {
      console.error("Error adding item:", err.response?.data || err);
    }
  };

  const removeMember = async (userId) => {
    try {
      await axios.delete(`${API_URL}/lists/${listId}/members/${userId}`, getAuthHeaders());
      const res = await axios.get(`${API_URL}/lists/${listId}`, getAuthHeaders());
      setMembers(res.data.members.sort((a, b) => a.joinedAt - b.joinedAt));
    } catch (err) {
      console.error("Error removing member:", err);
    }
  };

  const deleteList = async () => {
    try {
      await axios.delete(`${API_URL}/lists/${listId}`, getAuthHeaders());
      setNotification("Список удалён");
      setTimeout(() => navigate("/dashboard"), 2000);
    } catch (err) {
      console.error("Error deleting list:", err);
      setNotification("Ошибка при удалении списка");
    }
  };

  const updateListName = async () => {
    try {
      await axios.put(`${API_URL}/lists/${listId}`, { name: listName }, getAuthHeaders());
    } catch (err) {
      console.error("Error updating list name:", err);
    }
  };

  const copyShareLink = () => {
    const shareLink = `${window.location.origin}/join?listId=${listId}`;
    navigator.clipboard.writeText(shareLink);
    setNotification("Ссылка скопирована!");
    setTimeout(() => setNotification(""), 3000);
  };

  return (
    <div className="container">
      <button className="button-secondary" onClick={() => navigate("/dashboard")}>
        Назад
      </button>
      {notification && <div className="notification">{notification}</div>}
      <h1>
        <input
          type="text"
          value={listName}
          onChange={(e) => setListName(e.target.value)}
          onBlur={updateListName}
          disabled={ownerId !== currentUserId}
          className="list-title-input"
        />
      </h1>
      <p>Владелец: {ownerEmail}</p>
      {ownerId === currentUserId && (
        <button className="button-danger" onClick={deleteList}>
          Удалить список
        </button>
      )}
      <div className="list-section">
        <h2>Товары</h2>
        <input
          type="text"
          value={newItem}
          onChange={(e) => setNewItem(e.target.value)}
          placeholder="Добавить товар"
        />
        <button className="button-primary" onClick={addItem}>
          Добавить
        </button>
        <ul className="item-list">
          {items.map((item) => (
            <li key={item._id}>{item.name}</li>
          ))}
        </ul>
      </div>
      <div className="list-section">
        <h2>Участники</h2>
        <ul className="member-list">
          {members.map((member) => (
            <li key={member.userId}>
              {member.email}
              {ownerId === currentUserId && member.userId !== ownerId && (
                <button
                  className="button-danger"
                  onClick={() => removeMember(member.userId)}
                >
                  Удалить
                </button>
              )}
            </li>
          ))}
        </ul>
        <button className="button-primary" onClick={copyShareLink}>
          Поделиться
        </button>
      </div>
    </div>
  );
}

export default ShoppingList;
