import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5001";

function Dashboard() {
  const [lists, setLists] = useState([]);
  const [userEmail, setUserEmail] = useState("");
  const navigate = useNavigate();

  const getAuthHeaders = () => ({
    headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
  });

  useEffect(() => {
    // Получаем email пользователя
    axios
      .get(`${API_URL}/users/email/me`, getAuthHeaders())
      .then((res) => setUserEmail(res.data.email))
      .catch((err) => {
        console.error("Error fetching user:", err);
        navigate("/login");
      });

    // Получаем списки
    axios
      .get(`${API_URL}/lists`, getAuthHeaders())
      .then((res) => setLists(res.data))
      .catch((err) => {
        console.error("Error fetching lists:", err);
        if (err.response?.status === 401) navigate("/login");
      });
  }, [navigate]);

  const createList = () => {
    axios
      .post(`${API_URL}/lists`, {}, getAuthHeaders())
      .then((res) => navigate(`/list/${res.data.listId}`))
      .catch((err) => console.error("Error creating list:", err));
  };

  const logout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <div className="container">
      <div className="dashboard-header">
        <h1>Добро пожаловать, {userEmail}</h1>
        <button className="button-danger" onClick={logout}>
          Выйти
        </button>
      </div>
      <div>
        <button className="button-primary" onClick={createList}>
          Создать новый список
        </button>
        <button className="button-secondary" onClick={() => navigate("/join")}>
          Присоединиться к списку
        </button>
      </div>
      <h2>Мои списки</h2>
      {lists.length === 0 ? (
        <p>У вас пока нет списков.</p>
      ) : (
        <ul className="lists-grid">
          {lists.map((list) => (
            <li key={list.id} className="list-item">
              <a href={`/list/${list.id}`}>Список {list.id}</a>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default Dashboard;
