import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import io from "socket.io-client";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5001";

const getAuthHeaders = () => ({
  headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
});

function Dashboard() {
  const [userEmail, setUserEmail] = useState("");
  const [lists, setLists] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {

    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
      return;
    }

    // Получение email пользователя
    axios
      .get(`${API_URL}/users/email/me`, getAuthHeaders())
      .then((res) => setUserEmail(res.data.email))
      .catch((err) => {
        console.error("Error fetching user:", err);
        localStorage.removeItem("token");
        navigate("/login");
      });

    // Получение списков
    axios
      .get(`${API_URL}/lists`, getAuthHeaders())
      .then((res) => setLists(res.data))
      .catch((err) => console.error("Error fetching lists:", err));

    socket.on("kickedFromList", ({ listId: kickedListId }) => {
      console.log("Received kickedFromList on Dashboard:", kickedListId); // Отладка
      setLists((prevLists) => prevLists.filter((list) => list._id !== kickedListId));
      setNotification("Вы были удалены из списка");
      setTimeout(() => setNotification(""), 3000);
    });
  }, [navigate]);

  const createList = async () => {
    try {
      const res = await axios.post(`${API_URL}/lists`, { name: "Новый список" }, getAuthHeaders());
      console.log("Create list response:", res.data);
      navigate(`/list/${res.data.listId}`);
    } catch (err) {
      console.error("Error creating list:", err.response?.data || err);
    }
  };

  const deleteList = async (listId) => {
    try {
      await axios.delete(`${API_URL}/lists/${listId}`, getAuthHeaders());
      setLists(lists.filter((list) => list._id !== listId));
    } catch (err) {
      console.error("Error deleting list:", err);
    }
  };

  const logout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <div className="container">
      <div className="dashboard-header">
        <h1>Добро пожаловать, {userEmail}</h1>
        <button className="button-danger" onClick={logout}>
          Выйти
        </button>
      </div>
      <button className="button-primary" onClick={createList}>
        Создать список
      </button>
      <button className="button-primary" onClick={() => navigate("/join")}>
        Присоединиться к списку
      </button>
      <h2>Ваши списки</h2>
      <ul className="list-list">
        {lists.map((list) => (
          <li key={list._id}>
            <a href={`/list/${list._id}`}>{list.name}</a>
            {list.owner === localStorage.getItem("userId") && (
              <button
                className="button-danger"
                onClick={() => deleteList(list._id)}
              >
                Удалить
              </button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Dashboard;
