import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5001";

function JoinList() {
  const [listId, setListId] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const getAuthHeaders = () => ({
    headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
  });

  const handleJoin = async () => {
    if (!listId.trim()) {
      setError("Введите ID списка");
      return;
    }

    try {
      await axios.post(`${API_URL}/lists/${listId}/join`, {}, getAuthHeaders());
      setError("");
      navigate(`/list/${listId}`);
    } catch (err) {
      setError(err.response?.data?.error || "Ошибка при присоединении");
    }
  };

  return (
    <div className="auth-container">
      <h1>Присоединиться к списку</h1>
      {error && <p className="error">{error}</p>}
      <input
        type="text"
        value={listId}
        onChange={(e) => setListId(e.target.value)}
        placeholder="ID списка"
      />
      <button className="button-primary" onClick={handleJoin}>
        Присоединиться
      </button>
      <p>
        Вернуться к <a href="/dashboard">спискам</a>
      </p>
    </div>
  );
}

export default JoinList;
