import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useSearchParams } from "react-router-dom";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5001";

function JoinList() {
  const [listId, setListId] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const [hasJoined, setHasJoined] = useState(false); // Флаг для предотвращения двойного вызова

  const getAuthHeaders = () => ({
    headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
  });

  useEffect(() => {
    const idFromUrl = searchParams.get("listId");
    if (idFromUrl && !hasJoined) {
      setListId(idFromUrl);
      joinList(idFromUrl);
    }
  }, [searchParams, hasJoined]);

  const joinList = async (id) => {
    try {
      await axios.post(`${API_URL}/lists/join/${id || listId}`, {}, getAuthHeaders());
      setError("");
      setHasJoined(true); // Устанавливаем флаг после успешного вступления
      navigate(`/list/${id || listId}`);
    } catch (err) {
      setError(err.response?.data?.error || "Ошибка присоединения");
    }
  };

  const handleJoin = () => {
    if (!listId) {
      setError("Введите ID списка");
      return;
    }
    if (!hasJoined) {
      navigate(`/join?listId=${id || listId}`);
    }
  };

  return (
    <div className="container">
      <button className="button-secondary" onClick={() => navigate("/dashboard")}>
        Назад
      </button>
      <h1>Присоединиться к списку</h1>
      {error && <p className="error">{error}</p>}
      <input
        type="text"
        value={listId}
        onChange={(e) => setListId(e.target.value)}
        placeholder="ID списка"
      />
      <button className="button-primary" onClick={handleJoin}>
        Присоединиться
      </button>
    </div>
  );
}

export default JoinList;
