import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import io from "socket.io-client";
import axios from "axios";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5001";

function ShoppingList() {
  const [items, setItems] = useState([]);
  const [newItem, setNewItem] = useState("");
  const { listId } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    if (!listId) {
      // Создать новый список
      axios.post(`${API_URL}/lists`).then((res) => {
        navigate(`/list/${res.data.listId}`);
      });
      return;
    }

    // Загрузить список
    axios
      .get(`${API_URL}/lists/${listId}`)
      .then((res) => setItems(res.data.items))
      .catch((err) => console.error("Error loading list:", err));

    // Подключение к WebSocket
    const socket = io(API_URL);
    socket.emit("join_list", listId);

    socket.on("item_added", (item) => {
      setItems((prev) => [...prev, item]);
    });

    socket.on("item_updated", ({ id, purchased }) => {
      setItems((prev) =>
        prev.map((item) => (item.id === id ? { ...item, purchased } : item))
      );
    });

    socket.on("item_deleted", (itemId) => {
      setItems((prev) => prev.filter((item) => item.id !== itemId));
    });

    return () => {
      socket.disconnect();
    };
  }, [listId, navigate]);

  const addItem = () => {
    if (!newItem.trim()) return;
    axios
      .post(`${API_URL}/lists/${listId}/items`, { name: newItem })
      .then(() => setNewItem(""))
      .catch((err) => console.error("Error adding item:", err));
  };

  const togglePurchased = (id) => {
    const item = items.find((item) => item.id === id);
    if (!item) return;
    axios
      .patch(`${API_URL}/lists/${listId}/items/${id}`, {
        purchased: !item.purchased,
      })
      .catch((err) => console.error("Error updating item:", err));
  };

  const deleteItem = (id) => {
    axios
      .delete(`${API_URL}/lists/${listId}/items/${id}`)
      .catch((err) => console.error("Error deleting item:", err));
  };

  return (
    <div className="container">
      <h1>ShopSmart</h1>
      {listId && (
        <p className="share-link">
          Подalosиться: {`${window.location.origin}/list/${listId}`}
        </p>
      )}
      <div className="input-group">
        <input
          type="text"
          value={newItem}
          onChange={(e) => setNewItem(e.target.value)}
          placeholder="Добавить товар"
        />
        <button onClick={addItem}>Добавить</button>
      </div>
      <ul>
        {items.map((item) => (
          <li
            key={item.id}
            className={`item ${item.purchased ? "purchased" : ""}`}
          >
            <span onClick={() => togglePurchased(item.id)}>{item.name}</span>
            <button
              className="delete-button"
              onClick={() => deleteItem(item.id)}
            >
              Удалить
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ShoppingList;
