import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/Register.css";

const API_URL = "http://localhost:5001";

const Register = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState("");

  const handleRegister = async () => {
    if (password !== confirmPassword) {
      setError("Пароли не совпадают");
      setTimeout(() => setError(""), 3000);
      return;
    }
    try {
      console.log("Attempting registration with email:", email);
      const res = await axios.post(`${API_URL}/auth/register`, { email, password });
      console.log("Register response:", res.data);
      localStorage.setItem("token", res.data.token);
      localStorage.setItem("userId", res.data.userId); // Если userId нужен
      navigate("/dashboard");
    } catch (err) {
      console.error("Register error:", err.response?.data || err);
      const errorMessage = err.response?.data?.error || "Ошибка при регистрации";
      setError(errorMessage);
      setTimeout(() => setError(""), 3000);
    }
  };

  return (
    <div className="register">
      <h2>Регистрация в ShopSmart</h2>
      {error && <div className="error">{error}</div>}
      <input
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Email"
      />
      <input
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        placeholder="Пароль"
      />
      <input
        type="password"
        value={confirmPassword}
        onChange={(e) => setConfirmPassword(e.target.value)}
        placeholder="Подтвердите пароль"
      />
      <button onClick={handleRegister}>Зарегистрироваться</button>
      <p>
        Уже есть аккаунт?{" "}
        <span className="link" onClick={() => navigate("/login")}>
          Войти
        </span>
      </p>
    </div>
  );
};

export default Register;
