import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import io from "socket.io-client";
import axios from "axios";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5001";

function ShoppingList() {
  const [items, setItems] = useState([]);
  const [newItem, setNewItem] = useState("");
  const [inviteEmail, setInviteEmail] = useState("");
  const [users, setUsers] = useState([]);
  const { listId } = useParams();
  const navigate = useNavigate();

  const getAuthHeaders = () => ({
    headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
  });

  useEffect(() => {
    console.log("API_URL:", API_URL);
    console.log("listId:", listId);

    if (!listId) {
      navigate("/dashboard");
      return;
    }

    console.log("Fetching list:", listId);
    axios
      .get(`${API_URL}/lists/${listId}`, getAuthHeaders())
      .then(async (res) => {
        console.log("List fetched:", res.data);
        setItems(res.data.items);
        // Получаем пользователей списка
        const userPromises = res.data.list.userIds.map((userId) =>
          axios.get(`${API_URL}/users/${userId}`, getAuthHeaders())
        );
        const userResponses = await Promise.all(userPromises);
        setUsers(userResponses.map((r) => r.data.email));
      })
      .catch((err) => {
        console.error("Error loading list:", err.response?.data || err.message);
        if (err.response?.status === 401) navigate("/login");
      });

    const socket = io(API_URL, {
      withCredentials: true,
      auth: { token: localStorage.getItem("token") },
    });
    socket.emit("join_list", listId);

    socket.on("item_added", (item) => {
      setItems((prev) => [...prev, item]);
    });

    socket.on("item_updated", ({ id, purchased }) => {
      setItems((prev) =>
        prev.map((item) => (item.id === id ? { ...item, purchased } : item))
      );
    });

    socket.on("item_deleted", (itemId) => {
      setItems((prev) => prev.filter((item) => item.id !== itemId));
    });

    socket.on("user_added", ({ email }) => {
      setUsers((prev) => [...prev, email]);
    });

    socket.on("user_removed", ({ userId }) => {
      axios
        .get(`${API_URL}/users/${userId}`, getAuthHeaders())
        .then((res) => {
          setUsers((prev) => prev.filter((email) => email !== res.data.email));
        });
    });

    return () => {
      socket.disconnect();
    };
  }, [listId, navigate]);

  const addItem = () => {
    if (!newItem.trim()) return;
    axios
      .post(`${API_URL}/lists/${listId}/items`, { name: newItem }, getAuthHeaders())
      .then(() => setNewItem(""))
      .catch((err) => console.error("Error adding item:", err));
  };

  const togglePurchased = (id) => {
    const item = items.find((item) => item.id === id);
    if (!item) return;
    axios
      .patch(
        `${API_URL}/lists/${listId}/items/${id}`,
        { purchased: !item.purchased },
        getAuthHeaders()
      )
      .catch((err) => console.error("Error updating item:", err));
  };

  const deleteItem = (id) => {
    axios
      .delete(`${API_URL}/lists/${listId}/items/${id}`, getAuthHeaders())
      .catch((err) => console.error("Error deleting item:", err));
  };

  const inviteUser = () => {
    if (!inviteEmail.trim()) return;
    axios
      .post(
        `${API_URL}/lists/${listId}/invite`,
        { email: inviteEmail },
        getAuthHeaders()
      )
      .then(() => setInviteEmail(""))
      .catch((err) => console.error("Error inviting user:", err));
  };

  const removeUser = (email) => {
    axios
      .get(`${API_URL}/users/email/${email}`, getAuthHeaders())
      .then((res) => {
        axios
          .delete(`${API_URL}/lists/${listId}/users/${res.data._id}`, getAuthHeaders())
          .catch((err) => console.error("Error removing user:", err));
      })
      .catch((err) => console.error("Error fetching user:", err));
  };

  return (
    <div className="container">
      <h1>ShopSmart</h1>
      <p className="share-link">
        Поделиться: {`${window.location.origin}/list/${listId}`}
      </p>
      <div className="input-group">
        <input
          type="text"
          value={newItem}
          onChange={(e) => setNewItem(e.target.value)}
          placeholder="Добавить товар"
        />
        <button onClick={addItem}>Добавить</button>
      </div>
      <div className="input-group">
        <input
          type="email"
          value={inviteEmail}
          onChange={(e) => setInviteEmail(e.target.value)}
          placeholder="Пригласить пользователя (email)"
        />
        <button onClick={inviteUser}>Пригласить</button>
      </div>
      <h3>Участники:</h3>
      <ul>
        {users.map((email) => (
          <li key={email}>
            {email}
            <button onClick={() => removeUser(email)}>Удалить</button>
          </li>
        ))}
      </ul>
      <ul>
        {items.map((item) => (
          <li
            key={item.id}
            className={`item ${item.purchased ? "purchased" : ""}`}
          >
            <span onClick={() => togglePurchased(item.id)}>{item.name}</span>
            <button
              className="delete-button"
              onClick={() => deleteItem(item.id)}
            >
              Удалить
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ShoppingList;
