import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import io from "socket.io-client";
import axios from "axios";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5001";

function ShoppingList() {
  const [items, setItems] = useState([]);
  const [newItem, setNewItem] = useState("");
  const [users, setUsers] = useState([]);
  const [userId, setUserId] = useState("");
  const { listId } = useParams();
  const navigate = useNavigate();

  const getAuthHeaders = () => ({
    headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
  });

  useEffect(() => {
    if (!listId) {
      navigate("/dashboard");
      return;
    }

    // Получаем данные списка
    axios
      .get(`${API_URL}/lists/${listId}`, getAuthHeaders())
      .then((res) => {
        setItems(res.data.items);
        setUsers(res.data.users);
      })
      .catch((err) => {
        console.error("Error loading list:", err);
        if (err.response?.status === 401 || err.response?.status === 404) navigate("/dashboard");
      });

    // Получаем ID текущего пользователя
    axios
      .get(`${API_URL}/users/email/me`, getAuthHeaders())
      .then((res) => setUserId(res.data._id))
      .catch((err) => navigate("/login"));

    const socket = io(API_URL, {
      withCredentials: true,
      auth: { token: localStorage.getItem("token") },
    });
    socket.emit("join_list", listId);

    socket.on("item_added", (item) => {
      setItems((prev) => [...prev, item]);
    });

    socket.on("item_updated", ({ id, purchased }) => {
      setItems((prev) =>
        prev.map((item) => (item.id === id ? { ...item, purchased } : item))
      );
    });

    socket.on("item_deleted", (itemId) => {
      setItems((prev) => prev.filter((item) => item.id !== itemId));
    });

    socket.on("user_added", ({ email }) => {
      setUsers((prev) => [...prev, { email }]);
    });

    socket.on("user_removed", ({ userId }) => {
      axios
        .get(`${API_URL}/users/${userId}`, getAuthHeaders())
        .then((res) => {
          setUsers((prev) => prev.filter((user) => user.email !== res.data.email));
        });
    });

    socket.on("list_deleted", () => {
      navigate("/dashboard");
    });

    return () => {
      socket.disconnect();
    };
  }, [listId, navigate]);

  const addItem = () => {
    if (!newItem.trim()) return;
    axios
      .post(`${API_URL}/lists/${listId}/items`, { name: newItem }, getAuthHeaders())
      .then(() => setNewItem(""))
      .catch((err) => console.error("Error adding item:", err));
  };

  const togglePurchased = (id) => {
    const item = items.find((item) => item.id === id);
    if (!item) return;
    axios
      .patch(
        `${API_URL}/lists/${listId}/items/${id}`,
        { purchased: !item.purchased },
        getAuthHeaders()
      )
      .catch((err) => console.error("Error updating item:", err));
  };

  const deleteItem = (id) => {
    axios
      .delete(`${API_URL}/lists/${listId}/items/${id}`, getAuthHeaders())
      .catch((err) => console.error("Error deleting item:", err));
  };

  const removeUser = (user) => {
    axios
      .delete(`${API_URL}/lists/${listId}/users/${user._id}`, getAuthHeaders())
      .catch((err) => console.error("Error removing user:", err));
  };

  const deleteList = () => {
    if (window.confirm("Вы уверены, что хотите удалить список?")) {
      axios
        .delete(`${API_URL}/lists/${listId}`, getAuthHeaders())
        .then(() => navigate("/dashboard"))
        .catch((err) => console.error("Error deleting list:", err));
    }
  };

  return (
    <div className="container">
      <h1>ShopSmart: Список {listId}</h1>
      <p className="share-link">
        Поделиться: <span>{`${window.location.origin}/list/${listId}`}</span>
      </p>
      <button className="button-danger" onClick={deleteList}>
        Удалить список
      </button>
      <div className="list-container">
        {/* Товары */}
        <div className="list-section">
          <h2>Товары</h2>
          <div className="item-input-group">
            <input
              type="text"
              value={newItem}
              onChange={(e) => setNewItem(e.target.value)}
              placeholder="Добавить товар"
            />
            <button className="button-primary" onClick={addItem}>
              Добавить
            </button>
          </div>
          <ul>
            {items.map((item) => (
              <li
                key={item.id}
                className={`item ${item.purchased ? "purchased" : ""}`}
              >
                <span onClick={() => togglePurchased(item.id)}>{item.name}</span>
                <button className="button-danger" onClick={() => deleteItem(item.id)}>
                  Удалить
                </button>
              </li>
            ))}
          </ul>
        </div>
        {/* Пользователи */}
        <div className="list-section">
          <h2>Участники</h2>
          <ul>
            {users.map((user) => (
              <li key={user._id}>
                <span>{user.email}</span>
                {user._id !== userId ? (
                  <button
                    className="button-danger"
                    onClick={() => removeUser(user)}
                  >
                    Удалить
                  </button>
                ) : (
                  <span>(Вы)</span>
                )}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default ShoppingList;
