import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Dashboard.css";

const API_URL = "http://localhost:5001";

const Dashboard = () => {
  const navigate = useNavigate();
  const [lists, setLists] = useState([]);
  const [newListName, setNewListName] = useState("");
  const [notification, setNotification] = useState("");

  const getAuthHeaders = () => ({
    headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
  });

  const fetchLists = async () => {
    try {
      console.log("Fetching lists");
      const res = await axios.get(`${API_URL}/lists`, getAuthHeaders());
      console.log("Lists received:", res.data);
      setLists(res.data);
    } catch (err) {
      console.error("Error fetching lists:", err.response?.data || err);
      const errorMessage = err.response?.data?.error || "Ошибка при загрузке списков";
      setNotification(errorMessage);
      setTimeout(() => setNotification(""), 3000);
    }
  };

  const createList = async () => {
    if (!newListName) return;
    try {
      console.log("Creating list:", newListName);
      const res = await axios.post(
        `${API_URL}/lists`,
        { name: newListName },
        getAuthHeaders()
      );
      console.log("List created:", res.data);
      setLists([...lists, res.data]);
      setNewListName("");
      setNotification("Список создан!");
      setTimeout(() => setNotification(""), 3000);
    } catch (err) {
      console.error("Error creating list:", err.response?.data || err);
      const errorMessage = err.response?.data?.error || "Ошибка при создании списка";
      setNotification(errorMessage);
      setTimeout(() => setNotification(""), 3000);
    }
  };

  useEffect(() => {
    fetchLists();
  }, []);

  return (
    <div className="dashboard">
      <h2 className="dashboard-title">Your Shopping Lists</h2>
      {notification && <div className="notification">{notification}</div>}

      <div className="create-list">
        <input
          type="text"
          value={newListName}
          onChange={(e) => setNewListName(e.target.value)}
          placeholder="New list name..."
        />
        <button onClick={createList}>Create List</button>
      </div>

      <div className="list-grid">
        {lists.map((list) => (
          <div
            key={list._id}
            className="list-card"
            onClick={() => navigate(`/list/${list._id}`)}
          >
            <h3>{list.name}</h3>
            <p>{list.items?.length || 0} items</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
