import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { useParams, useNavigate, useLocation } from "react-router-dom";
import io from "socket.io-client";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5001";
const socket = io(API_URL, { autoConnect: false });

function ShoppingList() {
  const { listId } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const [listName, setListName] = useState("Новый список");
  const [items, setItems] = useState([]);
  const [members, setMembers] = useState([]);
  const [ownerId, setOwnerId] = useState("");
  const [ownerEmail, setOwnerEmail] = useState("");
  const [currentUserId, setCurrentUserId] = useState("");
  const [newItem, setNewItem] = useState("");
  const [notification, setNotification] = useState("");
  const isMounted = useRef(false);

  const getAuthHeaders = () => ({
    headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
  });

  useEffect(() => {
    if (isMounted.current) return;
    isMounted.current = true;

    if (location.state?.notification) {
      setNotification(location.state.notification);
      const timer = setTimeout(() => setNotification(""), 3000);
      return () => clearTimeout(timer);
    }

    const userId = localStorage.getItem("userId");
    if (!userId) {
      console.error("No userId in localStorage");
      navigate("/login");
      return;
    }

    socket.connect();
    console.log("Socket connecting for list:", listId, "user:", userId);
    socket.emit("joinList", listId);
    socket.emit("joinUser", userId);

    axios
      .get(`${API_URL}/lists/${listId}`, getAuthHeaders())
      .then((res) => {
        console.log("List data:", res.data);
        setItems(res.data.items || []);
        setMembers(res.data.members.sort((a, b) => a.joinedAt - b.joinedAt));
        setOwnerId(res.data.owner);
        setOwnerEmail(res.data.ownerEmail || "Неизвестно");
        setListName(res.data.name);
        setCurrentUserId(res.data.currentUserId);
      })
      .catch((err) => {
        console.error("Error fetching list:", err.response?.data || err);
        if (err.response?.status === 404) {
          navigate("/dashboard", { state: { notification: "Список не найден или вы были удалены" } });
        } else if (err.response?.status === 403) {
          navigate("/dashboard", { state: { notification: "Вы не участник этого списка" } });
        } else {
          navigate("/dashboard");
        }
      });

    socket.on("listUpdated", ({ items, members, name }) => {
      console.log("Received listUpdated:", { items, members, name });
      setItems(items || []);
      setMembers(members ? members.sort((a, b) => a.joinedAt - b.joinedAt) : []);
      if (name) setListName(name);
    });

    socket.on("kickedFromList", ({ listId: kickedListId, name }) => {
      console.log("Received kickedFromList:", { kickedListId, name }, "Current listId:", listId);
      if (kickedListId === listId) {
        navigate("/dashboard", { state: { notification: `Владелец списка удалил вас из "${name}"` } });
      }
    });

    return () => {
      console.log("Cleaning up socket listeners for list:", listId);
      socket.off("listUpdated");
      socket.off("kickedFromList");
      socket.disconnect();
      isMounted.current = false;
    };
  }, [listId, navigate, location.state]);

  const addItem = async () => {
    if (!newItem) return;
    try {
      const res = await axios.post(
        `${API_URL}/items/${listId}`,
        { name: newItem },
        getAuthHeaders()
      );
      setItems(res.data.items);
      socket.emit("updateList", { listId, items: res.data.items });
      setNewItem("");
    } catch (err) {
      console.error("Error adding item:", err.response?.data || err);
      setNotification(err.response?.data?.error || "Ошибка при добавлении товара");
      setTimeout(() => setNotification(""), 3000);
    }
  };

  const toggleItemCompletion = async (itemId, completed) => {
    try {
      console.log("Toggling item:", itemId, "to completed:", completed); // Лог для отладки
      const res = await axios.put(
        `${API_URL}/items/${itemId}/complete`,
        { completed },
        getAuthHeaders()
      );
      console.log("Updated items:", res.data.items);
      setItems(res.data.items);
      socket.emit("updateList", { listId, items: res.data.items });
    } catch (err) {
      console.error("Error toggling item completion:", err.response?.data || err);
      const errorMessage =
        err.response?.status === 404
          ? "Элемент или список не найден"
          : err.response?.data?.error || "Ошибка при обновлении статуса товара";
      setNotification(errorMessage);
      setTimeout(() => setNotification(""), 3000);
    }
  };

  const deleteCompletedItems = async () => {
    try {
      const res = await axios.delete(`${API_URL}/lists/${listId}/items/completed`, getAuthHeaders());
      setItems(res.data.items);
      socket.emit("updateList", { listId, items: res.data.items });
    } catch (err) {
      console.error("Error deleting completed items:", err.response?.data || err);
      setNotification(err.response?.data?.error || "Ошибка при удалении отмеченных товаров");
      setTimeout(() => setNotification(""), 3000);
    }
  };

  const removeMember = async (userId) => {
    try {
      await axios.delete(`${API_URL}/lists/${listId}/members/${userId}`, getAuthHeaders());
      const res = await axios.get(`${API_URL}/lists/${listId}`, getAuthHeaders());
      setMembers(res.data.members.sort((a, b) => a.joinedAt - b.joinedAt));
    } catch (err) {
      console.error("Error removing member:", err);
      setNotification(err.response?.data?.error || "Ошибка при удалении участника");
      setTimeout(() => setNotification(""), 3000);
    }
  };

  const deleteList = async () => {
    try {
      await axios.delete(`${API_URL}/lists/${listId}`, getAuthHeaders());
      navigate("/dashboard", { state: { notification: "Список удалён" } });
    } catch (err) {
      console.error("Error deleting list:", err);
      setNotification(err.response?.data?.error || "Ошибка при удалении списка");
      setTimeout(() => setNotification(""), 3000);
    }
  };

  const leaveList = async () => {
    if (ownerId === currentUserId) {
      setNotification("Владелец не может покинуть свой список. Удалите список, если хотите выйти.");
      setTimeout(() => setNotification(""), 3000);
      return;
    }
    try {
      await axios.post(`${API_URL}/lists/${listId}/leave`, {}, getAuthHeaders());
      navigate("/dashboard", { state: { notification: `Вы покинули список "${listName}"` } });
    } catch (err) {
      console.error("Error leaving list:", err.response?.data || err);
      setNotification(err.response?.data?.error || "Ошибка при выходе из списка");
      setTimeout(() => setNotification(""), 3000);
    }
  };

  const updateListName = async () => {
    try {
      await axios.put(`${API_URL}/lists/${listId}`, { name: listName }, getAuthHeaders());
    } catch (err) {
      console.error("Error updating list name:", err);
      setNotification(err.response?.data?.error || "Ошибка при обновлении названия");
      setTimeout(() => setNotification(""), 3000);
    }
  };

  const copyShareLink = () => {
    const shareLink = `${window.location.origin}/join?listId=${listId}`;
    navigator.clipboard.writeText(shareLink);
    setNotification("Ссылка скопирована!");
    setTimeout(() => setNotification(""), 3000);
  };

  return (
    <div className="container">
      <div className="list-header">
        <button className="button-secondary" onClick={() => navigate("/dashboard")}>
          Назад
        </button>
        <button className="button-danger" onClick={leaveList}>
          Выйти
        </button>
      </div>
      {notification && <div className="notification">{notification}</div>}
      <h1>
        <input
          type="text"
          value={listName}
          onChange={(e) => setListName(e.target.value)}
          onBlur={updateListName}
          disabled={ownerId !== currentUserId}
          className="list-title-input"
        />
      </h1>
      <p>Владелец: {ownerEmail}</p>
      {ownerId === currentUserId && (
        <button className="button-danger" onClick={deleteList}>
          Удалить список
        </button>
      )}
      <div className="list-section">
        <h2>Товары</h2>
        <input
          type="text"
          value={newItem}
          onChange={(e) => setNewItem(e.target.value)}
          placeholder="Добавить товар"
        />
        <button className="button-primary" onClick={addItem}>
          Добавить
        </button>
        <button className="button-danger" onClick={deleteCompletedItems}>
          Удалить отмеченные
        </button>
        <ul className="item-list">
          {items.map((item) => (
            <li key={item._id} className={item.completed ? "completed" : ""}>
              <input
                type="checkbox"
                checked={item.completed}
                onChange={(e) => toggleItemCompletion(item._id, e.target.checked)}
              />
              {item.name}
            </li>
          ))}
        </ul>
      </div>
      <div className="list-section">
        <h2>Участники</h2>
        <ul className="member-list">
          {members.map((member) => (
            <li key={member.userId}>
              {member.email}
              {ownerId === currentUserId && member.userId !== ownerId && (
                <button
                  className="button-danger"
                  onClick={() => removeMember(member.userId)}
                >
                  Удалить
                </button>
              )}
            </li>
          ))}
        </ul>
        <button className="button-primary" onClick={copyShareLink}>
          Поделиться
        </button>
      </div>
    </div>
  );
}

export default ShoppingList;
